import Header from "./components/Header"
import Tasks from "./components/Tasks"
import { useState,useEffect } from "react"
import AddTask from "./components/AddTask"
const App = () => {
  const [tasks,setTasks]=useState([])
const [showAdd , setShowAdd]=useState(false)
const [showRes , setShoeRes]=useState(false)

useEffect(()=>{
  const fetchTasks = async()=>{
    const res= await fetch('http://localhost:500/tasks')
    const data= await res.json()
  }
  fetchTasks()
})

// add Task
const addTask =(task)=>{
  const id= Math.floor(Math.random()*1000)+1
  const newTask= {id,...task}
  setTasks([...tasks,newTask])
}
// delete 
const deleteBtn= (id) =>{
   setTasks(tasks.filter((task)=>task.id !== id))
  console.log(id)
}
// toggle Reminder
const toggleReminder =(id) =>{
  setTasks(tasks.map((task)=>task.id == id ?{...task,reminder:!task.reminder}: task ))
  console.log(id)
}

  return (
    <div className="container">       
      <aside className="left"> 
      <h1>
        Task Tracker 
      </h1>

      </aside>
      <main className="main">
      <Header add={()=>setShowAdd(!showAdd)} app={()=>setShoeRes(!showRes)} show={showAdd} wash={showRes}/>

      {showAdd && <AddTask onAdd={addTask}/>}
      </main>
      <aside className="right">
      {showRes &&<Tasks  onToggle={toggleReminder} tasks={tasks} onDelete={deleteBtn} />}  

      </aside>

    </div>
  )
}

export default App
