// }
{/* <h1>   <button onClick={()=>increment(counter)}>+</button> {counter} */}
{/* <button onClick={()=>decrement(counter)}>-</button></h1> */}

  return (
    <div className="container">  
      <Header />
      {showAdd && <AddTask onAdd={addTask}/>}
      <Tasks  onToggle={toggleReminder} tasks={tasks} onDelete={deleteBtn}/>
      <h1  ></h1>
    </div>
  )
}

export default App
