import {useState} from "react"
const AddTask = () => {

  const [text, setText] = useState('')
  const  [day, setDay] = useState('')
  const  [reminder, setReminder] = useState(false)
  return (
    <form className='add-form'>
      <div className="form-control">
        <label htmlFor="">enter the task</label>
        <input type="text"  placeholder='enter the task' required value={text} onChange={(e)=>setText(e.target.value)}/>
      </div>
      <div className="form-control">
        <label htmlFor="">enter the time</label>
        <input type="text"  placeholder='enter the task' required value={day} onChange={(e)=>setDay(e.target.value)}/>
      </div>
      <div className="form-control">
        <label htmlFor="">enter the task</label>
        <input type="checkbox" className="btn btn-block"  value={reminder} onChange={(e)=>setReminder(e.currentTarget.value)} />
      </div>
    </form>
  )
}

export default AddTask
